import {
  AfterViewInit,
  ChangeDetectorRef,
  Component,
  OnInit,
  ViewChild,
} from "@angular/core";
import { MatPaginator } from "@angular/material/paginator";
import { MatSort } from "@angular/material/sort";
import { MatTableDataSource } from "@angular/material/table";
import { SubdivisionDataDisplayService } from "src/app/subdivision-data-display/subdivision-data-display.service";

import { FormsModule } from "@angular/forms";
import { MatInputModule } from "@angular/material/input";
import { MatSelectModule } from "@angular/material/select";
import { MatFormFieldModule } from "@angular/material/form-field";

export interface SubDivision {}

interface SubdivisionDataCode {
  value: string;
  viewValue: string;
}

@Component({
  selector: "app-subdivision-data-display",
  templateUrl: "./subdivision-data-display.component.html",
  styleUrls: ["./subdivision-data-display.component.css"],
  imports: [MatFormFieldModule, MatSelectModule, MatInputModule, FormsModule],
})
export class SubdivisionDataDisplayComponent implements OnInit, AfterViewInit {
  displayedColumns: string[] = ["id", "code", "name", "longitude", "latitude"];

  subdivisionDataCodes: SubdivisionDataCode[] = [
    { value: "steak-0", viewValue: "Steak" },
    { value: "pizza-1", viewValue: "Pizza" },
    { value: "tacos-2", viewValue: "Tacos" },
  ];

  sortType: string = "asc"; // defaoult value
  sortField: string = "id"; // defaoult value
  pageNum: number = 1;
  totalSubDivisions: number = 0;
  isLoading: boolean = false;
  subDivisions: Array<SubDivision> = [];

  dataSource = new MatTableDataSource<SubDivision>([]);

  //dataSource = MatTableDataSource<SubDivision>;

  @ViewChild(MatPaginator) paginator: MatPaginator = <MatPaginator>{};
  @ViewChild(MatSort) sort: MatSort = <MatSort>{};

  /*= new MatPaginator(
    new MatPaginatorIntl(),
    ChangeDetectorRef.prototype
  );*/

  constructor(private subDivisionService: SubdivisionDataDisplayService) {}

  ngOnInit(): void {
    this.loadData();
  }

  ngAfterViewInit() {
    //this.dataSource.paginator = this.paginator;
  }

  async loadData() {
    this.isLoading = true;
    this.subDivisionService
      .getAll(this.pageNum, this.sortField, this.sortType)
      .subscribe((result: any) => {
        //this.subDivisions = result["subdivisions"];
        //this.dataSource = result["subdivisions"];

        this.dataSource = new MatTableDataSource(result["subdivisions"]);
        this.dataSource.paginator = this.paginator;
        this.dataSource.sort = this.sort;

        //this.dataSource = new MatTableDataSource(this.subDivisions);
        /*
        if (result.status == "ok") {
          this.subDivisions = result.message.tasks;
          this.totalTasks = result.message.total_task_count;
        }*/
        this.isLoading = false;
      });
  }
}
