import { AfterViewInit, Component, OnInit, ViewChild } from "@angular/core";
import { MatPaginator } from "@angular/material/paginator";
import { MatSort } from "@angular/material/sort";
import { MatTableDataSource } from "@angular/material/table";
import { SubdivisionDataDisplayService } from "src/app/subdivision-data-display/subdivision-data-display.service";

export interface SubDivision {}

interface SubdivisionDataCode {
  value: string;
  viewValue: string;
}

@Component({
  selector: "app-subdivision-data-display",
  templateUrl: "./subdivision-data-display.component.html",
  styleUrls: ["./subdivision-data-display.component.css"],
})
export class SubdivisionDataDisplayComponent implements OnInit, AfterViewInit {
  displayedColumns: string[] = ["id", "code", "name", "longitude", "latitude"];

  subdivisionDataCodes: SubdivisionDataCode[] = [
    { value: "Builtout", viewValue: "Builtout" },
    { value: "Future", viewValue: "Future" },
    { value: "Active", viewValue: "Active" },
  ];

  isLoading: boolean = false;
  subDivisions: Array<SubDivision> = [];

  dataSource = new MatTableDataSource<SubDivision>([]);

  @ViewChild(MatPaginator) paginator: MatPaginator = <MatPaginator>{};
  @ViewChild(MatSort) sort: MatSort = <MatSort>{};

  constructor(private subDivisionService: SubdivisionDataDisplayService) {}

  ngOnInit(): void {
    this.loadData();
  }

  ngAfterViewInit() {}

  async loadData() {
    this.isLoading = true;
    this.subDivisionService.getAll().subscribe((result: any) => {
      this.dataSource = new MatTableDataSource(result["subdivisions"]);
      this.dataSource.paginator = this.paginator;
      this.dataSource.sort = this.sort;
      this.isLoading = false;
    });
  }
}
<p>Subdivision Data</p>
<mat-form-field>
  <mat-label>Filter on Subdivision Data Code:</mat-label>
  <mat-select (selectionChange)="selectDataCode($event.value)">
    <mat-option *ngFor="let subdivisionDataCode of subdivisionDataCodes" [value]="subdivisionDataCode.value">
      {{subdivisionDataCode.viewValue}}
    </mat-option>
  </mat-select>
</mat-form-field>

<table mat-table matSort [dataSource]="dataSource" class="subDivisions">
    <ng-container matColumnDef="id">
      <th mat-header-cell *matHeaderCellDef mat-sort-header> ID </th>
      <td mat-cell *matCellDef="let element"> {{element.id}} </td>
    </ng-container>

    <ng-container matColumnDef="code">
      <th mat-header-cell *matHeaderCellDef> CODE </th>
      <td mat-cell *matCellDef="let element"> {{element.code}} </td>
    </ng-container>

    <ng-container matColumnDef="name">
      <th mat-header-cell *matHeaderCellDef> NAME </th>
      <td mat-cell *matCellDef="let element"> {{element.name}} </td>
    </ng-container>

    <ng-container matColumnDef="longitude">
      <th mat-header-cell *matHeaderCellDef>  LONGITUDE </th>
      <td mat-cell *matCellDef="let element"> {{element.longitude}} </td>
    </ng-container>

    <ng-container matColumnDef="latitude">
      <th mat-header-cell *matHeaderCellDef>  LATITUDE </th>
      <td mat-cell *matCellDef="let element"> {{element.latitude}} </td>
    </ng-container>

    <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
    <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
</table>

<mat-paginator #paginator [pageSizeOptions]="[5, 10, 25, 100]" showFirstLastButtons></mat-paginator>
